                return (
                  <Step key={label}>
                    <StepButton
                      onClick={this.handleStep(index)}
                      completed={this.state.completed[index]}
                    >
                      {label.substr(0,3)}
                    </StepButton>
                  </Step>
                );
              })}
            </Stepper>
          </Grid>
          <Grid item md={5} xs={12}>
