import React, {Component} from 'react';
import PropTypes from "prop-types";
import classNames from "classnames";
import { withStyles } from "@material-ui/core/styles";


import Badge from "@material-ui/core/Badge";
import NotificationsIcon from "@material-ui/icons/Notifications";
import AccountCircle from "@material-ui/icons/AccountCircle";
import MoreIcon from "@material-ui/icons/MoreVert";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import ExitToApp from "@material-ui/icons/ExitToApp";
import Settings from "@material-ui/icons/Settings";
import HelpOutline from "@material-ui/icons/HelpOutline";
import Person from "@material-ui/icons/Person";
import Create from "@material-ui/icons/Create";
import Check from "@material-ui/icons/Check";
import Home from "@material-ui/icons/Home";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import MenuIcon from "@material-ui/icons/Menu";

const drawerWidth = 240;
const closedDrawerWidth = 73;


const styles = theme => ({
  grow: {
    flexGrow: 1
  },
  appBar: {
    width: `calc(100% - ${closedDrawerWidth}px)`,
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginLeft: 12,
    marginRight: 36
  },
  hide: {
    display: "none"
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
    whiteSpace: "nowrap"
  },
  sectionDesktop: {
    display: "none",
    [theme.breakpoints.up("md")]: {
      display: "flex"
    }
  },
  sectionMobile: {
    display: "flex",
    [theme.breakpoints.up("md")]: {
      display: "none"
    }
  },
  menuListDivider: {
    "border-top": "1px solid #8b8b8b2e"
  }
});



class Header extends Component {
      constructor(props){
        super(props);
        this.state = {
            open: this.props.open,
            anchorEl: null,
            mobileMoreAnchorEl: null
        };
    }

  handleDrawerToggle = () => {
    this.setState(prevState=>({ open: !prevState.open }));
  };

  handleProfileMenuOpen = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleMenuClose = () => {
    this.setState({ anchorEl: null });
    this.handleMobileMenuClose();
  };

  handleMobileMenuOpen = event => {
    this.setState({ mobileMoreAnchorEl: event.currentTarget });
  };

  handleMobileMenuClose = () => {
    this.setState({ mobileMoreAnchorEl: null });
  };
  render(){
    const { anchorEl, mobileMoreAnchorEl } = this.state;
    const { classes, theme, handleDrawerToggle } = this.props;
    const isMenuOpen = Boolean(anchorEl);
    const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);
    const renderMenu = (
      <Menu
        anchorEl={anchorEl}
        anchorOrigin={{ vertical: "top", horizontal: "right" }}
        transformOrigin={{ vertical: "top", horizontal: "right" }}
        open={isMenuOpen}
        onClose={this.handleMenuClose}
      >
        <MenuItem onClick={this.handleMenuClose}>
          <ListItemIcon>
            <Settings />
          </ListItemIcon>
          Mi perfil
        </MenuItem>
        <MenuItem
          onClick={this.handleMenuClose}
          style={{ "border-top": "1px solid #8b8b8b2e" }}
        >
          <ListItemIcon>
            <Person />
          </ListItemIcon>
          Referir amigos
        </MenuItem>
        <MenuItem onClick={this.handleMenuClose}>
          <ListItemIcon>
            <Create />
          </ListItemIcon>
          Configurar tema
        </MenuItem>
        <MenuItem onClick={this.handleMenuClose}>
          <ListItemIcon>
            <HelpOutline />
          </ListItemIcon>
          Ayuda
        </MenuItem>
        <MenuItem
          onClick={this.handleMenuClose}
          style={{ "border-top": "1px solid #8b8b8b2e" }}
        >
          <ListItemIcon>
            <Check />
          </ListItemIcon>
          Activar notificaciones
        </MenuItem>
        <MenuItem
          onClick={this.handleMenuClose}
          style={{ "border-top": "1px solid #8b8b8b2e" }}
        >
          <ListItemIcon>
            <ExitToApp />
          </ListItemIcon>
          Cerrar sesión
        </MenuItem>
      </Menu>
    );
    return (
      <div>
      <AppBar
        position="fixed"
        className={classNames(classes.appBar, {
          [classes.appBarShift]: this.state.open
        })}
      >
        <Toolbar disableGutters={!this.state.open}>
          <IconButton
            color="inherit"
            aria-label="Open drawer"
            onClick={this.handleDrawerToggle}
            className={classNames(classes.menuButton)}
          >
            <MenuIcon />
          </IconButton>
          <Typography
            style={{ width: "40%" }}
            variant="h6"
            color="inherit"
            noWrap
          >
            [Plan alimenticio]
            </Typography>
          <Typography color="inherit" style={{ width: "100%", "text-align": "center" }}>
            Miércoles 12 de diciembre. 5:13:02 PM{this.state.open&&("abierto")}
            </Typography>
          <div className={classes.grow} />
          <div className={classes.sectionDesktop}>
            <IconButton color="inherit">
              <Home />
            </IconButton>
            <IconButton color="inherit">
              <Badge badgeContent={17} color="secondary">
                <NotificationsIcon />
              </Badge>
            </IconButton>
            <IconButton
              aria-owns={isMenuOpen ? "material-appbar" : undefined}
              aria-haspopup="true"
              onClick={this.handleProfileMenuOpen}
              color="inherit"
            >
              <AccountCircle />
            </IconButton>
          </div>
          <div className={classes.sectionMobile}>
            <IconButton
              aria-haspopup="true"
              onClick={this.handleMobileMenuOpen}
              color="inherit"
            >
              <MoreIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>
      {renderMenu}
      </div>
    )
  }
}

Header.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired
};

export default withStyles(styles, { withTheme: true })(Header);
