import React from 'react';
import PropTypes from 'prop-types';
import deburr from 'lodash/deburr';
import Downshift from 'downshift';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import MenuItem from '@material-ui/core/MenuItem';

import {connect} from 'react-redux';
import Select from 'react-select';

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
];

class LookUpPatients extends React.Component {
  state = {
    selectedOption: null,
  }



  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  }
  render() {
    const { selectedOption } = this.state;
    const {patients}=this.props;
    const patientList = patients.map(function(patient){
      return {label: patient._id, value: patient.firstName+" Apellido"}
    })
  ;
    return (
      <Select
        value={selectedOption}
        onChange={this.handleChange}
        options={patientList}
      />
    );
  }
}



function mapStateToProps(state){
  return{
    patients: state.patients,
    currentUserId: state.currentUser.user.id
  };
};

export default connect(mapStateToProps, null)(LookUpPatients);
