import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import {fetchFoods} from '../../store/actions/foods';
import {connect} from 'react-redux';
import TableResult from '../../components/MealPlans/TableResult'

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
  dense: {
    marginTop: 19,
  },
  menu: {
    width: 200,
  },
});

class SearchBar extends React.Component {
  constructor(props){
    super(props);
      this.state={
        tableHide:true
      }
  }

  showsTable = () => {
    this.setState({ tableHide: false });
  };

  render() {
    const { classes, tableId } = this.props;

    return (
      <div>
        <TextField
          id="standard-name"
          placeholder="Agregar alimento..."
          className={classes.textField}
          onChange={(event) => {this.props.onSearchingFood(); this.showTable()}}
          margin="normal"
        />
        {!this.state.tableHide ? <TableResult/> : null}      </div>
    );
  }
}





function mapStateToProps(state){
  return{
    foods: state.foods,
    currentUserId: state.currentUser.user.id
  };
};

export default connect(mapStateToProps, {fetchFoods})(withStyles(styles)(SearchBar))
