import React, {Component} from 'react';
import {connect} from 'react-redux';
import { NavLink } from "react-router-dom";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Add from '@material-ui/icons/Add'
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import DateTimeRangePicker from '@wojtekmaj/react-datetimerange-picker';
import DateTimePicker from 'react-datetime-picker'
import Input from '@material-ui/core/Input';
import {apiCall} from '../../services/api'
import SearchDropdown from './SearchDropdown'

const styles = {
    dialogPaper: {
        minHeight: '85vh',
        maxHeight: '85vh',
    }
};


class Popup extends Component {
  state = {
    open: false,
    multiline:"",
    scheduledTime: new Date(),
    scheduledDuration: "",
    notes:"",
    patient:"",
  };

  handleSubmit = (e) => {
    e.preventDefault();
    //necesito lo siguiente:
      // scheduledTime: req.body.scheduledTime,
      // scheduledDuration: req.body.scheduledDuration,
      // notes: req.body.notes, (opcional)
      // expert: req.params.expert_id,
      // patient: req.body.patient_id 
      const { currentUserId } = this.props
      console.log(this.state.scheduledTime)
      console.log(this.state.scheduledDuration)
      console.log(this.state.notes)
      console.log(this.state.patient)
      console.log(currentUserId)

      apiCall("post", `/api/experts/${currentUserId}/scheduledappointments/`,{
        scheduledTime:this.state.scheduledTime,
        scheduledDuration:this.state.scheduledDuration,
        notes:this.state.notes,
        patient_id:this.state.patient,
      })
      .then(res => {
        console.log(res);
      })
      .catch(err => {
      console.log("No se puede agendar una consulta. Intente nuevamente")
      });
  }

  onPatientSelected = (patientId) => {
    this.setState({ patient: patientId.value });
  };

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value,
    });
  };

  onChange = scheduledTime => this.setState({ scheduledTime })

  render() {
    const {currentUserId, patient}=this.props
    const { classes } = this.props;

    return (
      <div>
        <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
          <Add/> Agendar cita
        </Button> 
        <Dialog
          classes={{paper:classes.dialogPaper}}
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">Agendar nueva cita</DialogTitle>
          <DialogContent>
            <form onSubmit={this.handleSubmit}>
              <Grid container direction="row" justify="space-between" alignItems="baseline">
                <Grid container direction="row" justify="space-between" alignItems="flex-start">
                  <Grid item md={8} xs={12}>  
                    <Grid container justify="space-between" alignItems="center">
                      <Grid item md={3} xs={4}>Paciente:</Grid>
                      <Grid item md={9} xs={8}>
                          <SearchDropdown patient={patient} onPatientSelected={this.onPatientSelected}/>
                      </Grid>
                    </Grid>
                  </Grid>  
                  <Grid item md={1} xs={12} align="center">o</Grid>
                  <Grid item md={3} xs={12} align="center">
                    <NavLink to="/pacientes">
                      <Button variant="outlined" color="primary" style={{margin: "5px 0px"}}>
                        <Add/>
                        Nuevo Paciente
                      </Button>
                    </NavLink>
                  </Grid>
                </Grid>
                <Grid container direction="row" justify="space-between" alignItems="flex-end" spacing={8}>
                  <Grid item md={2} xs={4}>Inicio:</Grid>
                  <Grid item md={10} xs={8}>
                    <DateTimePicker
                      onChange={this.onChange}
                      value={this.state.scheduledTime}
                      disableClock
                      clearIcon={null}
                    />              
                  </Grid>
                  <Grid item md={2} xs={4}>Fin:</Grid>
                  <Grid item md={10} xs={8}>
                    <DateTimePicker
                      onChange={this.onChange}
                      value={this.state.scheduledTime}
                      disableClock
                      clearIcon={null}
                      disabled
                    />              
                  </Grid>
                  <Grid item md={2} xs={4}>Duración:</Grid>
                  <Grid item md={10} xs={8}>
                        <Input
                          placeholder="Ej. 45 (minutos)"
                          value={this.state.scheduledDuration}
                          onChange={this.handleChange('scheduledDuration')}
                        />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      id="outlined-multiline-flexible"
                      label="Descripción (opcional)"
                      multiline
                      rowsMax="4"
                      value={this.state.notes}
                      onChange={this.handleChange('notes')}
                      margin="normal"
                      variant="outlined"
                      fullWidth
                    />
                  </Grid>
                </Grid>
              </Grid>
            </form>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="secondary">
              Cerrar
            </Button>
            <Button onClick={this.handleSubmit} variant="contained" color="primary">
              Agendar cita
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

Popup.propTypes = {
  classes: PropTypes.object.isRequired,
};

function mapStateToProps(state){
  return{
    patients: state.patients,
    currentUserId: state.currentUser.user.id
  };
};

export default connect(mapStateToProps, null)(withStyles(styles)(Popup));
