import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Route, Switch, Redirect} from 'react-router-dom';
import {authUser} from '../store/actions/auth';
import {removeError} from '../store/actions/errors';
import Header from './Header';
import Dashboard from './Dashboard';
import Sidebar from './Sidebar';


class Main extends Component{
  constructor() {
    super();
    this.state = {
      open: true
    };
  }

  handleDrawerToggle = () => {
    this.setState(prevState => ({ open: !prevState.open }));
  };

  render(){
    const {currentUser} = this.props;
    return(
      <div style={{display: 'flex'}}> {/*sin este flex se va a la mierda*/}
        {!currentUser.isAuthenticated &&  <Redirect to="/login"/> }

            <Header open={this.state.open} handleDrawerToggle={this.handleDrawerToggle} />
            <Sidebar open={this.state.open} />
            <Dashboard /> 


      </div>
    )
  }
}

function mapStateToProps(state) {
  return {
    currentUser: state.currentUser,
    errors: state.errors
  };
}

export default connect(mapStateToProps, {authUser, removeError})(Main);
