import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import { Alert } from 'antd';
import Grid from '@material-ui/core/Grid';

import 'antd/dist/antd.css';
import {Form, Icon, Input, Button, Checkbox,} from 'antd';


const FormItem = Form.Item;

class AuthForm extends Component {
  constructor(props){
    super(props);
    this.state={
      firstName:"",
      mail:"",
      password:""
    };
  }

  handleChange=(e) => {
    this.setState({
      [e.target.name]:e.target.value
    })
  }

  handleSubmit=(e) => {
    e.preventDefault();
    const authType= this.props.signUp? "signup":"login"
    this.props
      .onAuth(authType, this.state)
      .then(()=>{
          //te deber√≠a redirigir a un subdomain app.nutric.io/
          this.props.history.push('/')
      })
      .catch(()=>{
        return;
      })
  }

  render(){
    const {firstName, mail, password}=this.state;
    const {heading, buttonText, signUp, errors, history, removeError}=this.props;
    const { getFieldDecorator } = this.props.form;

    history.listen(()=>{
      removeError();
    })
    return (
      <div>
        <Grid container justify="center"alignItems="center">
          <Grid item md={12}>
          <Form className="login-form">
        <FormItem>
          {getFieldDecorator('userName', {
            rules: [{ required: true, message: 'Please input your username!' }],
          })(
            <Input prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />} placeholder="Username" />
          )}
        </FormItem>
        <FormItem>
          {getFieldDecorator('password', {
            rules: [{ required: true, message: 'Please input your Password!' }],
          })(
            <Input prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} type="password" placeholder="Password" />
          )}
        </FormItem>
        <FormItem>
          {getFieldDecorator('remember', {
            valuePropName: 'checked',
            initialValue: true,
          })(
            <Checkbox>Remember me</Checkbox>
          )}
          <a className="login-form-forgot" href="">Forgot password</a>
          <Button type="primary" htmlType="submit" className="login-form-button">
            Log in
          </Button>
          Or <a href="">register now!</a>
        </FormItem>
      </Form>
          </Grid>
          <Grid item  md={4} xs={12}>
          <div className='row'>
           {errors.message && <div>
              <Alert
                message="Error"
                description={errors.message}
                type="error"
                showIcon
              />
            </div>}
          </div>  
          </Grid>
        </Grid>
        <Grid container justify="center"alignItems="center">
          <Grid item md={6} xs={12}>
            <div className="row justify-content-md-center text-center">
              <div className="col-md6">
                <form onSubmit={this.handleSubmit}>
                  <h2>{heading}</h2>
                  {/*=====SIGN UP=======*/}
                  {signUp && (
                    <div>
                      <label htmlFor="password">Nombre:</label>
                      <input 
                          className="form-control"
                          id="firstName"
                          name="firstName"
                          onChange={this.handleChange}
                          type="text"
                      />
                    </div>
                  )}
                  <label htmlFor="mail">Correo electr√≥nico:</label>
                  <input 
                    className="form-control"
                    id="mail"
                    name="mail"
                    onChange={this.handleChange}
                    value={mail}
                    type="text"
                  />
                  <label htmlFor="password">Contrase√±a:</label>
                  <input 
                    className="form-control"
                    id="password"
                    name="password"
                    onChange={this.handleChange}
                    type="password"
                  />
                  <button className="btn btn-primary btn-block btn-lg">
                    {buttonText}
                  </button>

                  {!signUp && (
                      <Link to='/signup'>
                        <button className="btn btn-info btn-block btn-lg">
                          Quiero crearme una cuenta
                         </button>
                      </Link>
                  )}
                  {signUp && (
                      <Link to='/login'>
                        <button className="btn btn-info btn-block btn-lg">
                          Ya tengo una cuenta  
                        </button>
                      </Link>
                  )}
                </form>
              </div>
            </div>
          </Grid>
        </Grid>
      </div>
    )
  }
}

export default AuthForm;
