import React from 'react';
import {Switch, Route, withRouter,Redirect} from 'react-router-dom';
import {connect} from 'react-redux';
import Homepage from '../components/Homepage'
import AuthForm from '../components/AuthForm'
import {authUser} from '../store/actions/auth'
import {removeError} from '../store/actions/errors'
import Navbar from '../containers/Navbar'


const Main = props => {
  const {authUser, errors, removeError, currentUser}=props;
  return (

      <div className="container">
      <Navbar/>
        <Switch>
          <Route exact path="/" render={props => <Homepage currentUser={currentUser} {...props} />}/>
          <Route 
            exact 
            path="/login" 
            render={props => {
              return(
                <AuthForm removeError={removeError} errors={errors} onAuth={authUser} buttonText="Inicia Sesión" heading="Welcome Back." {...props} />
              );
            }}
          />
          <Route 
            exact 
            path="/signup" 
            render={props => {
              return(
                <AuthForm removeError={removeError} errors={errors} onAuth={authUser} signUp buttonText="Registro" heading="Únete a Nutric.io" {...props} />
              );
            }}
          />
        </Switch>
      </div>
    )
}

function mapStateToProps(state) {
  return {
    currentUser: state.currentUser,
    errors:state.errors
  }
}


export default withRouter(connect(mapStateToProps,{authUser, removeError})(Main))
