import {ADD_ERROR, REMOVE_ERROR} from '../actionTypes.js';  

let INITIAL_STATE = {message: null}

export default (state = INITIAL_STATE, action) => {
  switch(action.type){
    case ADD_ERROR: 
      return { ...state, message: action.error }
    case REMOVE_ERROR: 
      return { ...state, message: null }
    default: 
      return state
  }
}
