import React, {Component} from 'react';
import {connect} from 'react-redux';
import { NavLink } from "react-router-dom";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Add from '@material-ui/icons/Add'
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import DateTimeRangePicker from '@wojtekmaj/react-datetimerange-picker';
import DateTimePicker from 'react-datetime-picker'
import Input from '@material-ui/core/Input';
import {apiCall} from '../../services/api'
import SearchDropdown from './SearchDropdown'

const styles = {
    dialogPaper: {
        minHeight: '80vh',
        maxHeight: '80vh',
    },
};


class Popup extends Component {
  state = {
    open: false,
    multiline:"",
    scheduledTime: new Date(),
    scheduledDuration: 0,
    notes:"",
    patient:"",
  };

  handleSubmit = (e) => {
    e.preventDefault();
    //necesito lo siguiente:
      // scheduledTime: req.body.scheduledTime,
      // scheduledDuration: req.body.scheduledDuration,
      // notes: req.body.notes, (opcional)
      // expert: req.params.expert_id,
      // patient: req.body.patient_id 
      console.log(this.state.scheduleData)
      console.log(this.state.scheduledTime)
      console.log(this.state.notes)
      console.log(this.state.patient)
      console.log(this.state.currentUserId)


      // const {currentUser}=this props
      // apiCall("get", `/api/experts/${expert_id}/scheduledappointments`,scheduleData)
      // .then(res => {
      //   dispatch(loadFoods(res));
      // })
      // .catch(err => {
      //   dispatch(addError(err.message));
      // });
  }

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value,
    });
  };


  onChange = scheduledTime => this.setState({ scheduledTime })

  render() {
    const { classes } = this.props;

    return (
      <div>
        <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
          <Add/> Agendar cita
        </Button> 
        <Dialog
          classes={{paper:classes.dialogPaper}}
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">Agendar nueva cita</DialogTitle>
          <DialogContent>
            <form onSubmit={this.handleSubmit}>
              <Grid container direction="row" justify="space-between" alignItems="baseline">
                <Grid container direction="row" justify="space-between" alignItems="flex-start" spacing={40}>
                  <Grid item xs={7}>  
                    <Grid container justify="space-between" alignItems="center">
                      <Grid item xs={2}>Paciente:</Grid>
                      <Grid item xs={8}>
                        <SearchDropdown/>
                      </Grid>
                    </Grid>
                  </Grid>  
                  <Grid item xs={1} align="center">o</Grid>
                  <Grid item xs={4}>
                    <NavLink to="/pacientes">
                      <Button variant="outlined" color="primary">
                        <Add/>
                        Nuevo Paciente
                      </Button>
                    </NavLink>
                  </Grid>
                </Grid>
                <Grid container direction="row" justify="space-between" alignItems="flex-end" spacing={40}>
                  <Grid item xs={2}>Fecha:</Grid>
                  <Grid item xs={10}>
                    <DateTimePicker
                      onChange={this.onChange}
                      value={this.state.scheduledTime}
                      disableClock
                    />              
                  </Grid>
                  <Grid item xs={2}>Duración:</Grid>
                  <Grid item xs={10}>
                        <Input
                          placeholder="Ej. 45 (minutos)"
                        />           
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      id="outlined-multiline-flexible"
                      label="Descripción (opcional)"
                      multiline
                      rowsMax="4"
                      value={this.state.multiline}
                      onChange={this.handleChange('multiline')}
                      margin="normal"
                      variant="outlined"
                      fullWidth
                    />
                  </Grid>
                </Grid>
              </Grid>
            </form>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="secondary">
              Cerrar
            </Button>
            <Button onClick={this.handleSubmit} variant="contained" color="primary">
              Agendar cita
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

function mapStateToProps(state){
  return{
    patients: state.patients,
    currentUserId: state.currentUser.user.id
  };
};

export default connect(mapStateToProps, null)(withStyles(styles)(Popup));
