import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import Add from '@material-ui/icons/Add'
import Grid from '@material-ui/core/Grid';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';


function getModalStyle() {
  const top = 50
  const left = 50

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const styles = theme => ({
  paper: {
    position: 'absolute',
    width: theme.spacing.unit * 70,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
  },
});

class SimpleModal extends Component {
  state = {
    open: false,
    multiline:" "
  };

  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value,
    });
  };
  render() {
    const { classes } = this.props;
    const {title}=this.props
    return (
      <div>
        <Button onClick={this.handleOpen}><Add/>Agendar Consulta</Button>
        <Modal
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
          open={this.state.open}
          onClose={this.handleClose}
        >
          <div style={getModalStyle()} className={classes.paper}>
            <Typography variant="h6" id="modal-title">
              {title}
            </Typography>
            <Grid container direction="row" justify="space-between" alignItems="baseline">
              <Grid item xs={5}>Paciente:....</Grid>
              <Grid item xs={2}>o</Grid>
              <Grid item xs={5}>
                <Button variant="contained" color="primary">
                  <Add/>
                  Nuevo Paciente
                </Button>
              </Grid>
              <Grid item xs={5}>Fecha:</Grid>
              <Grid item xs={7}>____:</Grid>
              <Grid item xs={5}>Hora de Inicio:</Grid>
              <Grid item xs={7}>_____:</Grid>
              <Grid item xs={12}>
                <TextField
                  id="outlined-multiline-flexible"
                  label="DescripciÃ³n (opcional)"
                  multiline
                  rowsMax="4"
                  value={this.state.multiline}
                  onChange={this.handleChange('multiline')}
                  margin="normal"
                  variant="outlined"
                  fullWidth
                />
              </Grid>
            </Grid>
            <Button>Agendar consulta</Button>
          </div>
        </Modal>
      </div>
    );
  }
}

SimpleModal.propTypes = {
  classes: PropTypes.object.isRequired,
};

// We need an intermediary variable for handling the recursive nesting.
const SimpleModalWrapped = withStyles(styles)(SimpleModal);

export default SimpleModalWrapped;
