import React, {Component} from 'react';
import { Provider } from 'react-redux';
import { configureStore } from '../store';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import jwtDecode from "jwt-decode";
import '../../App.css';
import { setAuthorizationToken, setCurrentUser, authUser } from "../store/actions/auth";

import AuthPage from './AuthPage';
import Header from './Header';
import Sidebar from './Sidebar';
import Dashboard from "./Dashboard";

// hacer handler para routes no especificadas. ej /weigvuwgeuv que bote error 



const store = configureStore(); 

if (localStorage.jwtToken) {
  setAuthorizationToken(localStorage.jwtToken);
  // prevent someone from manually tampering with the key of jwtToken in localStorage
  try {
    store.dispatch(setCurrentUser(jwtDecode(localStorage.jwtToken)));
  } catch (e) {
    store.dispatch(setCurrentUser({}));
  }
}


class App extends Component {
  constructor() {
    super();
    this.state = {
      open: true
    };
  }

  handleDrawerToggle = () => {
    this.setState(prevState => ({ open: !prevState.open }));
  };

  render(){
   return(
    <Provider store={store}>
      <Router>
        <div style={{"display": "flex"}}>
          {/* 
            Tenemos que displayear en Dashboard SOLAMENTE lo relacionado al software.
            Los authforms están de más. Quizás separarlo con un Route /home/login /home/signup
            Y renderear el resto del software if(loggeado)
          */}
          <Header 
            open={this.state.open}
            handleDrawerToggle={this.handleDrawerToggle}
          />
          <Sidebar open={this.state.open}/>
          <Dashboard /> 
        </div>
      </Router>

      <Router>
          <AuthPage />
      </Router>
    </Provider>
    )
  }
}

export default App;
