import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Redirect} from 'react-router-dom';
import { NavLink } from "react-router-dom";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Add from '@material-ui/icons/Add'
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Input from '@material-ui/core/Input';
import {apiCall} from '../../services/api'
import IconButton from '@material-ui/core/IconButton';
import PersonAdd from '@material-ui/icons/PersonAdd'
import CloseIcon from '@material-ui/icons/Close';

import "antd/dist/antd.css";
import { TimePicker } from "antd";
import { DatePicker } from "antd";

const styles = {
    dialogPaper: { //dimensiones del popup
        minHeight: '85vh',
        maxHeight: '85vh',
    }
};


class PopupAddPatient extends Component {
  state = {
    open: false,
    multiline:"",
    firstName:"",
    lastName: "",
    email: "",
    birthDate: ""
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { currentUserId } = this.props

    // apiCall("post", `/api/experts/${currentUserId}/scheduledappointments/`,{
    //   scheduledTimeStart:scheduledTimeStart,
    //   scheduledTimeEnd:scheduledTimeEnd,
    //   scheduledTimeDuration:duration,
    //   notes:this.state.notes,
    //   patient_id:this.state.patient,
    // })
    // .then(res => {
    //   console.log(res);
    //   // ¿Cómo se redirigía con history?
    //   window.location.href="/agenda"
    // })
    // .catch(err => {
    //   console.log(err)
    // });
  }



  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value,
    });
  };

  render() {
    const {currentUserId, patient}=this.props
    const { classes } = this.props;

    return (
      <div>
        <IconButton onClick={this.handleClickOpen}>
          <PersonAdd />
        </IconButton>
        <Dialog
          classes={{paper:classes.dialogPaper}}
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">
            Agregar paciente
          </DialogTitle>
          <DialogContent>
            <DialogContentText style={{marginBottom: '15px'}}>
              Completa el formulario con los datos del nuevo paciente.
            </DialogContentText>
            <form onSubmit={this.handleSubmit}>
              <Grid container direction='row' >

                <Grid container alignItems='center'>
                  <Grid item xs={2}>Nombre:</Grid>
                  <Grid item xs={10}>
                    <Input
                      placeholder="ej: María"
                      className={classes.input}
                      inputProps={{
                        'aria-label': 'Description',
                      }}
                    />
                  </Grid>
                </Grid>

                <Grid container alignItems='center'>
                  <Grid item xs={2}>Apellido:</Grid>
                  <Grid item xs={10}>
                    <Input
                      placeholder="ej: García"
                      className={classes.input}
                      inputProps={{
                        'aria-label': 'Description',
                      }}
                    />
                  </Grid>
                </Grid>

                <Grid container alignItems='center'>
                  <Grid item xs={2}>Email:</Grid>
                  <Grid item xs={10}>
                    <Input
                      placeholder="ej: m.garcia@gmail.com"
                      className={classes.input}
                      inputProps={{
                        'aria-label': 'Description',
                      }}
                    />
                  </Grid>
                </Grid>

                <Grid container alignItems='center'>
                  <Grid item xs={2}>Fecha de nacimiento</Grid>
                  <Grid item xs={10}>
                    <DatePicker
                      value={this.state.date}
                      onChange={this.handleDateChange}
                      format={"DD/MMMM/YYYY"}
                      placeholder="Fecha"
                      showToday={false}
                      disabledDate={this.disabledDate}
                      allowClear={false}
                    />
                  </Grid>
                </Grid>

              </Grid>
            </form>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="secondary">
              Cerrar
            </Button>
            <Button onClick={this.handleSubmit} variant="contained" color="primary">
              Agendar cita
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

PopupAddPatient.propTypes = {
  classes: PropTypes.object.isRequired,
};

function mapStateToProps(state){
  return{
    patients: state.patients,
    currentUserId: state.currentUser.user.id
  };
};

export default connect(mapStateToProps, null)(withStyles(styles)(PopupAddPatient));
