import { apiCall } from '../../services/api';
import { addError } from './errors';
import { LOAD_PATIENTS } from '../actionTypes';

export const loadPatients = (patients) => ({
  type: LOAD_PATIENTS,
  patients: patients
});



export const fetchPatients = (expert_id) => {
  return (dispatch) => {
    return apiCall("get", `/api/experts/${expert_id}/patients`)
      .then(res => {
        dispatch(loadPatients(res));
      })
      .catch(err => {
        dispatch(addError(err.message));
      });

  };
};

export const addPatient = (
  expert_id, 
  firstName, 
  lastName, 
  mail, 
  phone,
  gender,
  birthDate,
  nationality,
  idNumber,
  address, 
  ) => (dispatch, getState) => {
  return apiCall("post", `/api/expert/${expert_id}/patients`, { firstName, lastName, mail, phone, gender, birthDate, nationality, idNumber, address })
    .then(res => (console.log('added patient with the following data: ' + res)))
    .catch(err => console.log(err))
};
