import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import { Alert } from 'antd';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import LockIcon from '@material-ui/icons/LockOutlined';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import withStyles from '@material-ui/core/styles/withStyles';

const styles = theme => ({
  main: {
    width: 'auto',
    display: 'block', // Fix IE 11 issue.
    marginLeft: theme.spacing.unit * 3,
    marginRight: theme.spacing.unit * 3,
    [theme.breakpoints.up(400 + theme.spacing.unit * 3 * 2)]: {
      width: 400,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  paper: {
    marginTop: theme.spacing.unit * 8,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: `${theme.spacing.unit * 2}px ${theme.spacing.unit * 3}px ${theme.spacing.unit * 3}px`,
  },
  avatar: {
    margin: theme.spacing.unit,
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing.unit,
  },
  submit: {
    marginTop: theme.spacing.unit * 3,
  },
});

class AuthForm extends Component {
  constructor(props){
    super(props);
    this.state={
      firstName:"",
      mail:"",
      password:""
    };
  }

  handleChange=(e) => {
    this.setState({
      [e.target.name]:e.target.value
    })
  }

  handleSubmit=(e) => {
    e.preventDefault();
    const authType= this.props.signUp? "signup":"login"
    this.props
      .onAuth(authType, this.state)
      .then(()=>{
          //te deber√≠a redirigir a un subdomain app.nutric.io/
          this.props.history.push('/')
      })
      .catch(()=>{
        return;
      })
  }

  render(){
    const {firstName, mail, password}=this.state;
    const {heading, buttonText, signUp, errors, history, removeError}=this.props;
    history.listen(()=>{
      removeError();
    })
    const { classes } = props;

    return (
      <div>
        <Grid container justify="center"alignItems="center">
          <Grid item  md={4} xs={12}>
          <div className='row'>
           {errors.message && <div>
              <Alert
                message="Error"
                description={errors.message}
                type="error"
                showIcon
              />
            </div>}
          </div>  
          </Grid>
        </Grid>
        <Grid container justify="center"alignItems="center">
          <Grid item md={6} xs={12}>
            <div className="row justify-content-md-center text-center">
              <div className="col-md6">
                <form onSubmit={this.handleSubmit}>
                  <h2>{heading}</h2>
                  {/*=====SIGN UP=======*/}
                  {signUp && (
                    <div>
                      <label htmlFor="password">Nombre:</label>
                      <input 
                          className="form-control"
                          id="firstName"
                          name="firstName"
                          onChange={this.handleChange}
                          type="text"
                      />
                    </div>
                  )}
                  <label htmlFor="mail">Correo electr√≥nico:</label>
                  <input 
                    className="form-control"
                    id="mail"
                    name="mail"
                    onChange={this.handleChange}
                    value={mail}
                    type="text"
                  />
                  <label htmlFor="password">Contrase√±a:</label>
                  <input 
                    className="form-control"
                    id="password"
                    name="password"
                    onChange={this.handleChange}
                    type="password"
                  />
                  <button className="btn btn-primary btn-block btn-lg">
                    {buttonText}
                  </button>

                  {!signUp && (
                      <Link to='/signup'>
                        <button className="btn btn-info btn-block btn-lg">
                          Quiero crearme una cuenta
                         </button>
                      </Link>
                  )}
                  {signUp && (
                      <Link to='/login'>
                        <button className="btn btn-info btn-block btn-lg">
                          Ya tengo una cuenta  
                        </button>
                      </Link>
                  )}
                </form>
              </div>
            </div>
          </Grid>
        </Grid>
      </div>
    )
  }
}

AuthForm.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(AuthForm);
